import React, { useState } from 'react'
import QCard from './QCard'
import "./Question.css"
import ShowCard from './ShowCard';
const Question = () => {
    const [TotalScore, setTotalScore] = useState(0);
    const [TimeArr, setTimeArr] = useState([]);
    const question = [
        {
            "ques": "I came out in cold sweat",
            "options": ["I ws really nervous or afrad","b=I still don't understand","I was running out of water","None of Above"],
            "hint": ["dd"],
            "answer": 2
        },
        {
            "ques": "Question2",
            "options": ["a","b","c","d"],
            "hint": ["dd"],
            "answer": 3
        },
        {
            "ques": "Question3",
            "options": ["a","b","c","d"],
            "hint": ["dd"],
            "answer": 1
        },
    ]
    const [i, seti] = useState(0);
  return (
    <>
    <div className="Question">Question</div>
    {
        i<3 ? <QCard data = {question[i]} i = {i} seti={seti} TotalScore={TotalScore} setTotalScore={setTotalScore} setTimeArr={setTimeArr} TimeArr={TimeArr}/> : 
        <ShowCard TotalScore={TotalScore} TimeArr={TimeArr}/>
    }
    </>
  )
}

export default Question
