import React from 'react'
import "./QCard.css"
import  { useState } from 'react'
import { useEffect } from 'react';


const QCard = ({data, i, seti, TotalScore,setTotalScore, TimeArr, setTimeArr}) => {
  function handleVisibilityChange() {
    if (document.visibilityState === 'hidden') {
      // The user has switched away from the tab
      console.log('Tab switched away');
      seti(3);
    } else {
      // The user has switched back to the tab
      console.log('Tab switched back');
    }
  }
  useEffect(() => {
    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, []);
 const [NoClue, setNoClue] = useState(0);
 const [Time, setTime] = useState(Date.now());      
 const [clueArr, setclueArr] = useState({
    0: false,
    1: false,
    2: false
 })
   const openClue = (key) =>{
      if(NoClue>1)
      {
        seti(3);
      }
      setclueArr(prevState=>({
         ...prevState, [key]: true
      }))
      setNoClue(NoClue+1)
      setTotalScore(TotalScore-1);
      console.log(clueArr)
   }
    const nextquestion = () =>{
        setTimeArr(prevState=>[
            ...prevState,Date.now()-Time
        ])
        console.log(TimeArr)
        setTime(Date.now())
        seti(i+1);
    }
    const check = (index) =>{
        if(index+1 === data.answer)
        {
            // alert("Right answer");
            setTotalScore(TotalScore+10);
        }
        else{
             setTotalScore(TotalScore-2);
            // alert("Wrong answer");
        }
        nextquestion();
    }
  return (
    <div>
       {/* QCard  {i }
       <button onClick={()=> seti(i+1)}>lick me</button>
       {data.ques}  */}
       <div className="card">
        <div className='ques'>Ques {i}: {data.ques}</div>
        {data.hint.map((clue,key)=>{
             return (<div>
             {console.log(clue)}
                <div className='clue' onClick={()=>openClue(key)}>{clueArr[`${key}`] ? clue : `clue ${key+1}`}</div>
             </div>)
        })}
        <div className='option_block'>
        {data.options.map((option,index) => {
            return (
                <div className="options" onClick={()=>check(index)}>{option}</div> 
            )
        })}
        </div>
       <button onClick={()=>nextquestion()}>Next Question</button>
       </div>
    </div>
  )
}

export default QCard
